import React from 'react';
import Notification from '../dashboard/Notification'
import { connect } from 'react-redux'
import { firestoreConnect } from 'react-redux-firebase'
import { compose } from 'redux'

const CreateDetail = (props) => {
  const id = props.match.params.id
  const {authorFirstName, authorLastName, content, title} = props.project

  console.log(props)
  return (
    <div className='container project details'>
      <div className="header">
        <h2>Title {id}</h2>
      </div>
      <div className="row">
        <div className="leftcolumn">
          <div className="card">
            <h5>{title}</h5>
            <p>{content}</p>
          </div>
        </div>
      </div>
      <div className="rightcolumn">
        <div className="card">
          <Notification />
        </div>
      </div>
    </div>
  );
};

const mapState = (state, ownProps) => {
  // console.log(state)
  const id = ownProps.match.params.id //allows us to get the id from the route but on its own props which can be passed as a second parameter
  const projects = state.firestore.data.projects
  const project = projects ? projects[id] : null
  return {
    project: project

  }
}

export default compose(
  connect(mapState),
  firestoreConnect([
    { collection: 'projects' }
  ])
)(CreateDetail);
